module Tradsim
  # TODO: Make a class so the we can have a reusable object to translate more text
  def self.to_sim(trad)
  	mapping(trad, true)
  end

  def self.to_trad(sim)
  	mapping(sim, false)
  end

  def self.guess(text)
  	traditional = simplified = unknown = 0
  	text.split(//).each do |char|
  		trad_char_char_index = trad_char.index(char)
  		sim_char_char_index = sim_char.index(char)
  		if trad_char_char_index.nil? && sim_char_char_index.nil?
  			unknown = unknown + 1
  		elsif trad_char_char_index.nil?
  			simplified = simplified + 1
  		elsif sim_char_char_index.nil?
  			traditional = traditional + 1
  		else
  			unknown = unknown + 1
  		end
  	end
  	output = :traditional
  	if (simplified > traditional)
  		output = :simplified
  	end
  	output
  end

  def self.toggle(text)
  	guess_result = guess(text)
  	if guess_result == :traditional
  		output = mapping(text, true)
  	else
  		output = mapping(text, false)
  	end
  	output
  end

